window.addEventListener("load",()=>{populateCodeBlocks();highlightSelectedCodeBlock();});window.addEventListener("hashchange",()=>{highlightSelectedCodeBlock();});function populateCodeBlocks(){for(const snippetsPre of document.querySelectorAll("pre[ebnf-snippet]")){const id=snippetsPre.getAttribute("ebnf-snippet");if(id){const codeBlock=document.getElementById(id)?.querySelector("code");if(codeBlock){codeBlock.replaceChildren(...snippetsPre.childNodes);snippetsPre.remove();}}}}
function highlightSelectedCodeBlock(){for(const codeBlock of document.querySelectorAll("code")){codeBlock.style.background="var(--md-code-bg-color)";}
const id=location.hash.replace("#","");const codeBlock=document.getElementById(id)?.querySelector("code");if(codeBlock){codeBlock.style.background="var(--md-code-hl-color)";}}